import axios from 'axios';
import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`

    width: 100%;
    height: 100%;
    position: absolute;
    background: rgba(0,0,0,.7);
    top: 0;
    align-items: center;
    overflow: hidden;
    z-index: 99;

    #modal {
        width: 550px;
        height: 300px;
        margin: 0 auto;
        background: #FFF;
        padding: 30px;
        position: relative;
        top: 50%;
        transform: translateY(-50%);
        border-radius: 10px;
        border: 2px solid #2d4a22;
    }

    #fechar {
        position: absolute;
        top: 0;
        right: 15px;
        font-size: 35px;
        transform: rotate(45deg);
        cursor: pointer;
    }

    form {
        display: flex;
        justify-content: space-between;

        div {
            display: flex;
            flex-direction: column;
            width: 45%;
            margin-top: 45px;

            input {
                padding: 10px;
                margin-top: 5px;
            }
        }

        button {
            position: absolute;
            bottom: 40px;
            right: 35px;
            padding: 15px;
            background: #2d4a22;
            color: #FFF;
            border: none;
            border-radius: 8px;
        }
    }

`;

function Modal({ show, close }) {


    // const [email, setEmail] = useState("");
    // const [telefone, setTelefone] = useState("");

    // function handleSubmit(event) {
    //     const novoContato = {
    //         email: email,
    //         telefone: telefone
    //     }

    //     event.preventDefault();
    // }

    // function onSubmit(event) {

    // }

    // const handleChangeEmail = (e) => {
    //     setEmail(e.target.value);
    // }

    // const handleChangeTelefone = (e) => {
    //     setTelefone(e.target.value);
    // }

    const contato = {
        email: "fernandofer@gmail.com",
        telefone: "98659655"
    }

    function cadastra() {

        axios.post('http://localhost:8080/contatos', contato).then((response) => {
            console.log(contato)
        })
    }



  return (
      <Container style={{
          display: show ? 'block' : 'none'
      }}>
          <div id="modal">
              <div id="fechar" onClick={close}>+</div>

              <form>
                  <div>
                      <label>Email</label>
                      <input name="email" type="text"  placeholder="Email" required="required" />
                  </div>

                  <div>
                      <label>Telefone</label>
                      <input name="telefone" type="text"  placeholder="telefone" required="required" />
                  </div>

                  <button type="submit">Adicionar contato</button>
                  <button onClick={cadastra}>Clique</button>
              </form>
          </div>
      </Container>
  );
}

export default Modal;
