import React from 'react';
import styled from 'styled-components';

const Container = styled.div`

    width: 100%;
    height: 100%;
    position: absolute;
    background: rgba(0,0,0,.7);
    top: 0;
    align-items: center;
    overflow: hidden;
    z-index: 99;

    #modal {
        width: 550px;
        height: 300px;
        margin: 0 auto;
        background: #FFF;
        padding: 30px;
        position: relative;
        top: 50%;
        transform: translateY(-50%);
        border-radius: 10px;
        border: 2px solid #2d4a22;
    }

    #fechar {
        position: absolute;
        top: 0;
        right: 15px;
        font-size: 35px;
        transform: rotate(45deg);
        cursor: pointer;
    }

    form {
        display: flex;
        justify-content: space-between;

        div {
            display: flex;
            flex-direction: column;
            width: 45%;
        }
    }

`;

function Modal() {
  return (
      <Container>
          <div id="modal">
              <div id="fechar">+</div>

              <form>
                  <div>
                      <label>Email</label>
                      <input name="email" type="text" placeholder="Email" required="required" />
                  </div>

                  <div>
                      <label>Telefone</label>
                      <input name="telefone" type="text" placeholder="teelfone" required="required" />
                  </div>
              </form>
          </div>
      </Container>
  );
}

export default Modal;
